const nodemailer = require('nodemailer');

// setup the transporter object
let transporter = nodemailer.createTransport({
  service: 'gmail',
  auth: {
    user: 'aktesting98@gmail.com',
    pass: 'testMailing.98'
  }
});

// setup the mailOptions
let mailOptions = {
  from: 'aktesting98@gmail.com',
  to: 'infernoxl98@gmail.com',
  subject: "Testing one last time",
  text: 'This is working',
  attachments: [
    {
      file: 'test.txt',
      path: __dirname+'/test.txt'
      //path: folderName + "/" + fileName
    }
  ]
};

// sending the email, with validation of success
transporter.sendMail(mailOptions, function(err, data) {
  if (err) {
    console.log('Error Occurs', err);
  } else {
    console.log('Email sent!!!');
  }
});


